import React, {useEffect} from 'react';
import { Grid } from '@mui/material';
import alanBtn from "@alan-ai/alan-sdk-web";

import Details from './components/Details/Details';
import Main from './components/Main/Main';

import {styles} from './style';

function App() {

  const classes = {styles};
  const ALAN_Key = `8d9e1496853de4b8f82e5f85e5ade1f72e956eca572e1d8b807a3e2338fdd0dc/stage`
  useEffect(() => {
    alanBtn({
        key: ALAN_Key,
        onCommand: (commandData) => {
          if(commandData.command === 'type'){
            setFormData({...formData, type: e.target.value})(commandData.data)
          }
          if(commandData.command === 'category'){
            setFormData(commandData.data)
          }
          if(commandData.command === 'amount'){
            setFormData(commandData.data)
          }
        }
    });
  }, []);
  return (
      <div>
        <Grid css={classes.grid} container spacing={2} alignItems='center' justifyContent='center' style={{height: '100vh'}}>

          <Grid item xs={12} sm={3}>
            <Details title="Income"/>
          </Grid>

          <Grid item xs={12} sm={4}>
            <Main title="Main"/>
          </Grid>

          <Grid item xs={12} sm={3}>
            <Details title="Expense"/>
          </Grid>
        </Grid>
      </div>
  );
}

export default App;
