import React, { useContext } from 'react'
import { List as MUIList, ListItem, ListItemAvatar, ListItemText, Avatar, IconButton, Slide, ListItemSecondaryAction } from '@mui/material'
import { Delete, MonetizationOn } from '@mui/icons-material'

import { ExpenseTrackerContext } from '../../../context/context'

const List = () => {

    const { transactions, deleteTransaction } = useContext(ExpenseTrackerContext);

    const groupedTransactions = transactions.reduce((acc, transaction) => {
        const year = transaction.date.getFullYear();
        const month = transaction.date.toLocaleString('en-US', { month: 'long' });
        if (!acc[year]) {
            acc[year] = {};
        }
        if (!acc[year][month]) {
            acc[year][month] = [];
        }
        acc[year][month].push(transaction);
        return acc;
    }, {});

    return (
        <MUIList dense={false} style={{ maxHeight: '150px', overflow: 'auto' }}>
        {Object.entries(groupedTransactions).map(([year, months]) => (
            <Slide direction="down" in mountOnEnter unmountOnExit key={year}>
                <ListItem>
                    <ListItemText primary={year} />
                    <MUIList dense={false}>
                        {Object.entries(months).map(([month, transactions]) => (
                            <Slide direction="down" in mountOnEnter unmountOnExit key={month}>
                                <ListItem>
                                    <ListItemText primary={month} />
                                    <MUIList dense={false}>
                                        {transactions.map((transaction) => (
                                            <ListItem key={transaction.id}>
                                                <ListItemAvatar>
                                                    <Avatar style={{ backgroundColor: transaction.type === 'Expense' ? 'red' : 'green' }}>
                                                        <MonetizationOn/>
                                                    </Avatar>
                                                </ListItemAvatar>
                                                <ListItemText primary={transaction.category} secondary={`$${transaction.amount} - ${transaction.date}`} />
                                                <ListItemSecondaryAction>
                                                    <IconButton edge="end" aria-label="delete" onClick={() => deleteTransaction(transaction.id)}>
                                                        <Delete/>
                                                    </IconButton>
                                                </ListItemSecondaryAction>
                                            </ListItem>
                                        ))}
                                    </MUIList>
                                </ListItem>
                            </Slide>
                        ))}
                    </MUIList>
                </ListItem>
            </Slide>
        ))}
    </MUIList>


  )
}

export default List;
