import { css } from '@emotion/react';

export const styles = (theme) => ({
  desktop: css`
    ${theme.breakpoints.up('sm')} {
      display: none;
    }
  `,
  mobile: css`
    ${theme.breakpoints.down('sm')} {
      display: none;
    }
  `,
  main: css`
    ${theme.breakpoints.up('sm')} {
      paddingBottom: 5%;
    }
  `,
  last: css`
    ${theme.breakpoints.down('sm')} {
      marginBottom: ${theme.spacing(3)};
      paddingBottom: 200px;
    }
  `,
  grid: css`
    & > * {
      margin: ${theme.spacing(2)};
    }
  `,
});
