import styled from '@emotion/styled';

const StyledComponent = styled('div')(({ theme }) => ({
  income: {
    borderBottom: '10px solid rgba(0, 255, 0, 0.5)',
  },
  expense: {
    borderBottom: '10px solid rgba(255, 0, 0, 0.5)',
  },
}));

export default StyledComponent;
