import { css } from '@emotion/react';

const styles = (theme) => ({
  desktop: css`
    ${theme.breakpoints.up('sm')} {
      display: none;
    }
  `,
  mobile: css`
    ${theme.breakpoints.down('sm')} {
      display: none;
    }
  `,
  main: css`
    ${theme.breakpoints.up('sm')} {
      padding-bottom: 5%;
    }
  `,
  last: css`
    ${theme.breakpoints.down('sm')} {
      margin-bottom: ${theme.spacing(3)};
      padding-bottom: 200px;
    }
  `,
  grid: css`
    & > * {
      margin: ${theme.spacing(2)};
    }
  `,
});

export default styles;
