import { css } from '@emotion/react';

export const styles = (theme) => ({
  desktop: css`
    @media (min-width: ${theme.breakpoints.values.sm}px) {
      display: none;
    }
  `,
  mobile: css`
    @media (max-width: ${theme.breakpoints.values.sm - 1}px) {
      display: none;
    }
  `,
  main: css`
    @media (min-width: ${theme.breakpoints.values.sm}px) {
      paddingBottom: '5%';
    }
  `,
  last: css`
    @media (max-width: ${theme.breakpoints.values.sm - 1}px) {
      marginBottom: ${theme.spacing(3)};
      paddingBottom: 200px;
    }
  `,
  grid: css`
    & > * {
      margin: ${theme.spacing(2)};
    }
  `,
});
