/** @jsxImportSource @emotion/react */
import styled from '@emotion/styled';
import React from 'react';
import { TextField, Typography, Grid, Button, FormControl, Select, MenuItem } from '@mui/material';

const FormContainer = styled.div`
  width: 100%;

  .radioGroup {
    display: flex;
    justify-content: center;
    margin-bottom: -10px;
  }

  .button {
    margin-top: 20px;
  }

  .fullWidthFormControl {
    width: 100%;
  }

  .selectLabel {
    transform: translate(14px, 12px) scale(1);
  }
`;

const Form = () => {
  return (
    <FormContainer>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Typography align='center' variant='subtitle2' gutterBottom>
            {/* ... Your Typography content */}
          </Typography>
        </Grid>
        <Grid item xs={6}>
          <FormControl className="fullWidthFormControl">
            <Typography variant="body1" color="textSecondary" className="selectLabel">
              Type
            </Typography>
            <Select label="Type">
              <MenuItem value="Income">Income</MenuItem>
              <MenuItem value="Expense">Expense</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <FormControl className="fullWidthFormControl">
            <Typography variant="body1" color="textSecondary" className="selectLabel">
              Category
            </Typography>
            <Select label="Category">
              <MenuItem value="Business">Business</MenuItem>
              <MenuItem value="Salary">Salary</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <TextField type='number' label='Amount' fullWidth />
        </Grid>
        <Grid item xs={6}>
          <TextField type='date' label='Date' fullWidth />
        </Grid>
        <Button className="button" variant='outlined' color='primary' fullWidth>
          Create
        </Button>
      </Grid>
    </FormContainer>
  );
};

export default Form;
