import React, { useReducer, createContext } from "react";

import contextReducer from './contextReducer';

const initialState = JSON.parse(localStorage.getItem('transactions')) || [];

export const ExpenseTrackerContext = createContext(initialState);

export const Provider = ({children}) => {
    const [transactions, dispatch] = useReducer(contextReducer, initialState);

    // Action Creators
    const deleteTransaction = (id) => {
        dispatch({ type: 'DELETE_TRANSACTION', payload: id });
    }

    const addTransaction = (transaction) => {
        dispatch({ type: 'ADD_TRANSACTION', payload: transaction });
    }

    const balance = transactions.reduce((acc, currVal) => (currVal.type === 'Expense' ? acc - currVal.amount : acc + currVal.amount), 0);

    const ALAN_Key = `8d9e1496853de4b8f82e5f85e5ade1f72e956eca572e1d8b807a3e2338fdd0dc/stage`
    useEffect(() => {
        alanBtn({
            key: ALAN_Key,
            onCommand: (commandData) => {
            if(commandData.command === 'type'){
                setFormData({type: commandData.data})
            }
            if(commandData.command === 'category'){
                setFormData({category: commandData.data})
            }
            if(commandData.command === 'amount'){
                setFormData({amount: commandData.data})
            }
            if(commandData.command === 'date'){
                setFormData({date: formatDate(commandData.data)})
            }
            }
        });
    }, [ALAN_Key]);

    //console.log(transactions);

    return(
        <ExpenseTrackerContext.Provider value={{ 
            transactions,
            balance,
            deleteTransaction, 
            addTransaction
        }}>
            {children}
        </ExpenseTrackerContext.Provider>
    )
}
