import { css } from '@emotion/react';
import styled from '@emotion/styled';

const useStyles = css`
  .radioGroup {
    display: flex;
    justify-content: center;
    margin-bottom: -10px;
  }

  .button {
    margin-top: 20px;
  }
`;

const StyledFormControl = styled(FormControl)`
  width: 100%;
`;

const StyledSelect = styled(Select)`
  label {
    transform: translate(14px, 12px) scale(1);
  }
`;
